          /> */}

          <button type="submit" className="btn btn-primary">
            RESET
          </button>
          <p>
            <NavLink to="/login" href="#" style={{ textDecoration: "none" }}>
              <i className="fas fa-arrow-left"></i> back{" "}
            </NavLink>
          </p>
        </form>
      </div>
    </Layout>
  );
};

export default ForgotPasssword;
